var path = require('path')

module.exports = {
  url (filename) {
    filename = path.basename(filename)
    return filename.split('.')[0]
      .split('@@').join('/')
      .split('@').join('.')
      .split('!_!').join('#')
      .split('!__!').join('?')
  },
  name (urlFragment) {
    urlFragment = urlFragment.replace('http://','').replace('https://','')
    return urlFragment
      .split('/').join('@@')
      .split('.').join('@')
      .split('#').join('!_!')
      .split('?').join('!__!')
  }
}
